  onStateChange: function(aWebProgress, aRequest, aStateFlags, aStatus) {
    if (aStateFlags & 0x10) { // finished, failed or canceled
      progress.parentNode.selectedIndex = 0;
      consoleLog(waveformFile.leafName + " downloaded.");
      draw();
    }
  }
}
